   149â†’<script setup lang="ts">
   150â†’import { ref, computed } from 'vue'
   151â†’import type { Initiative, QuarterConfig, TeamMember, Assignment } from '~/types'
   152â†’import { checkCarryOver, getInitiativeWarnings, getRoleFulfillment } from '~/utils/capacityCalculator'
   153â†’import AssignmentCell from './AssignmentCell.vue'
   154â†’
   155â†’const props = defineProps<{
   156â†’  initiative: Initiative
