   193â†’function getProgressColor(percentage: number): string {
   194â†’  if (percentage >= 100) return 'success'
   195â†’  if (percentage >= 75) return 'info'
   196â†’  if (percentage >= 50) return 'warning'
   197â†’  return 'error'
   198â†’}
   199â†’
   200â†’function getMember(memberId: string): TeamMember | undefined {
