   202â†’}
   203â†’
   204â†’function getAssignmentsForWeek(weekNumber: number): Assignment[] {
   205â†’  return props.initiative.assignments.filter((assignment) => {
   206â†’    const endWeek = assignment.startWeek + assignment.weeksAllocated - 1
