   208â†’  })
   209â†’}
   210â†’
   211â†’function isAssignmentCarryover(assignment: Assignment, currentWeek: number): boolean {
   212â†’  if (!props.quarter) return false
   213â†’  const carryOverInfo = checkCarryOver(assignment, props.quarter)
   214â†’  return carryOverInfo.carriesOver && currentWeek > props.quarter.totalWeeks - carryOverInfo.carriedWeeks
   215â†’}
   216â†’
   217â†’function getAssignmentIndex(assignment: Assignment): number {
   218â†’  return props.initiative.assignments.findIndex((a) => a === assignment)
   219â†’}
   220â†’
   221â†’// Drag and drop handlers for the row
   222â†’function handleDragOver(event: DragEvent) {
   223â†’  if (props.draggedMember) {
   224â†’    isDragOver.value = true
   225â†’    if (event.dataTransfer) {
   226â†’      event.dataTransfer.dropEffect = 'copy'
   227â†’    }
   228â†’  }
   229â†’}
   230â†’
   231â†’function handleDragLeave() {
