   233â†’}
   234â†’
   235â†’function handleDrop(event: DragEvent) {
   236â†’  isDragOver.value = false
   237â†’
   238â†’  if (!props.draggedMember) return
   239â†’
   240â†’  // Find a matching role requirement
   241â†’  const memberRoles = props.draggedMember.roles
