   243â†’    (req) => memberRoles.includes(req.role)
   244â†’  )
   245â†’
   246â†’  const startWeek = 1 // Default to first week when dropping on the row
   247â†’
   248â†’  if (matchingRequirement) {
   249â†’    emit('drop-member', {
   250â†’      initiative: props.initiative,
   251â†’      memberId: props.draggedMember.id,
   252â†’      role: matchingRequirement.role,
   253â†’      startWeek,
   254â†’    })
   255â†’  } else if (props.initiative.roleRequirements.length > 0) {
