   288â†’  // Find a matching role requirement
   289â†’  const memberRoles = props.draggedMember.roles
   290â†’  const matchingRequirement = props.initiative.roleRequirements.find(
   291â†’    (req) => memberRoles.includes(req.role)
   292â†’  )
   293â†’
   294â†’  if (matchingRequirement) {
   295â†’    emit('drop-member', {
   296â†’      initiative: props.initiative,
   297â†’      memberId: props.draggedMember.id,
   298â†’      role: matchingRequirement.role,
   299â†’      startWeek: week,
   300â†’    })
   301â†’  } else if (props.initiative.roleRequirements.length > 0) {
   302â†’    // Use first role requirement if no match
   303â†’    emit('drop-member', {
   304â†’      initiative: props.initiative,
