   306â†’      role: props.initiative.roleRequirements[0].role,
   307â†’      startWeek: week,
   308â†’    })
   309â†’  }
   310â†’}
   311â†’
   312â†’function handleAssignmentDragStart(event: DragEvent, assignment: Assignment) {
