// Use the board drag and drop composable
const {
  dragState,
  dropTarget,
  isDragging,
  setDropTarget,
  isDropTarget,
  handleDropOnWeek,
  endDrag,
  getDropRole,
} = useBoardDragDrop()

// Local drag over state for row-level feedback
const isDragOver = ref(false)

// Computed to check which week is the drop target
const isDragOverWeek = computed(() => {
  if (!isDragging.value || !dropTarget.value) return null
  if (dropTarget.value.initiativeId === props.initiative.id) {
    return dropTarget.value.week
  }
  return null
})

